mapper.select:
SELECT
	DISTINCT sgg_name
FROM sungeogu
WHERE sg_id = #{SG_ID}
AND sg_type_code = #{SG_TYPE_CODE}
AND sd_name = #{SD_NAME}

AND wiw_name = #{WIW_NAME}

SELECT
	DISTINCT CASE wiw_name WHEN '' THEN '전지역' ELSE wiw_name END AS wiw_name
FROM sungeogu
WHERE sg_id = #{SG_ID}
AND sg_type_code = #{SG_TYPE_CODE}
AND sd_name = #{SD_NAME}
