mapper.select:
SELECT DISTINCT sd_name AS sdName
, gusigun_name AS gusigunName
, emd_name AS emdName
, sgg_name AS sggName
, sg_id AS sgId , sg_type_code AS sgTypeCode FROM region WHERE 1=1
AND sd_name = #{sdName}
AND gusigun_name = #{gusigunName}
AND emd_name = #{emdName}
AND sg_id = '20200415' AND sg_type_code = '2' ORDER BY sd_name, gusigun_name, emd_name

SELECT
	sgg_name AS sggName
FROM region
WHERE 1=1
AND sd_name = #{sdName}
AND gusigun_name = #{gusigunName}
AND emd_name = #{emdName}
AND sg_id = '20200415'
AND sg_type_code = '2'
