mapper.select:
SELECT
	*
FROM woomin_vote
WHERE 1=1
AND sg_id = #{SG_ID}
AND sg_type_code = #{SG_TYPE_CODE}
AND sd_name = #{SD_NAME}
AND wiw_name != '합계'
ORDER BY vr_order

